import React, { useState } from "react";

const DataIngestion = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files).map((file) => ({
      name: file.name,
      size: (file.size / 1024).toFixed(2) + " KB",
      date: new Date().toLocaleDateString(),
    }));

    setFiles([...files, ...uploadedFiles]);
  };

  return (
    <div className="flex flex-col min-h-screen">
      <div className="flex flex-col items-center justify-center flex-grow p-6">
        <input
          type="file"
          multiple
          onChange={handleFileUpload}
          className="hidden"
          id="fileUpload"
        />
        <label
          for="fileUpload"
          className="p-3 bg-blue-500 text-white rounded-lg text-lg font-semibold cursor-pointer"
        >
          Bulk Import
        </label>
        <div className="mt-6 w-full max-w-md">
          <h2 className="text-xl font-bold text-center mb-4">Uploaded Files</h2>
          <ul className="border rounded p-4 bg-gray-100">
            {files.map((file, index) => (
              <li key={index} className="p-2 border-b last:border-b-0">
                {file.name} - {file.size} - {file.date}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default DataIngestion;
