import React, { useState } from "react";

const DataIngestion = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files).map((file) => ({
      name: file.name,
      size: (file.size / 1024).toFixed(2) + " KB",
      date: new Date().toLocaleDateString(),
    }));

    setFiles([...files, ...uploadedFiles]);
  };

  return (
    <div className="flex flex-col min-h-screen">
      <div className="flex flex-col items-center justify-center flex-grow p-6">
        <input
          type="file"
          multiple
          onChange={handleFileUpload}
          className="hidden"
          id="fileUpload"
        />
        <label
          for="fileUpload"
          className="p-3 bg-blue-500 text-white rounded-lg text-lg font-semibold cursor-pointer"
        >
          Bulk Import
        </label>
        <div className="mt-6 w-full max-w-md">
          <h2 className="text-xl font-bold text-center mb-4">Uploaded Files</h2>
          <table className="w-full border-collapse border border-gray-300 bg-white">
            <thead>
              <tr className="bg-gray-200">
                <th className="border border-gray-300 p-2">File Name</th>
                <th className="border border-gray-300 p-2">Size</th>
                <th className="border border-gray-300 p-2">Date</th>
              </tr>
            </thead>
            <tbody>
              {files.map((file, index) => (
                <tr key={index} className="text-center">
                  <td className="border border-gray-300 p-2">{file.name}</td>
                  <td className="border border-gray-300 p-2">{file.size}</td>
                  <td className="border border-gray-300 p-2">{file.date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DataIngestion;
